export function num2base62(n: number) {
    let r = '';
    while (n) {
        const v = n % radix;
        // console.log({ n, v, r });
        r = letters[v] + r;
        n = (n - v) / radix;
    }
    return r;
}

export function num2base92(n: number) {
    if (n === 0) return letters92[0];
    let r = '';
    while (n) {
        const v = n % radix92;
        // console.log({ n, v, r });
        r = letters92[v] + r;
        n = (n - v) / radix92;
    }
    return r;
}

export function decodeJa3(s: string | null) {
    const ret = { pwd: ``, cont: ``, decoded: `` };
    const err = { ...ret, decoded: `error` };
    if (s === null) {
        console.log(`decodeJa3("${s}") nothing to decode`);
        return err;
    }
    try {
        const pwdStr = s.slice(0, 9);
        // tslint:disable-next-line: radix
        const pwd = parseInt(pwdStr);
        const cont = s.slice(9);
        let roll = pwd,
            r = '';
        for (let i = 0; i < cont.length; ++i) {
            roll = lcg(roll);
            const c = roll % SAFE_ROLL;
            //console.log({ i, roll, c });
            r += String.fromCharCode(cont.charCodeAt(i) - c);
        }
        return { pwd, cont, decoded: r };
    } catch (e: any) {
        console.error(`decodeJa3("${s}") error:`, e.message);
        return err;
    }
}

export function str2hex(str: string, joiner = `:`) {
    return str.split(``).map(a => a.charCodeAt(0).toString(16)).join(joiner);
}

export function hex2str(hex: string, joiner = `:`) {
    return hex.split(joiner).map(a => String.fromCharCode(parseInt(a, 16))).join(``);
}

export function timeFromId(id: string) {
    if (!isValidId(id)) return -1;
    return parseInt(id.slice(0, 9), 36);
}

export function genId(tail = 3, timestamp = 0, seed: number | null = null, randStr: string | null = null) {
    const rand = mulberry32(seedIfNeeded32(seed));
    return () => {
        // function log(a: string) { if (!dumped) trace(() => a); }
        let tailStr = ``;
        while (tailStr.length < tail) tailStr += (randStr || num2base62(rand()));
        // log(`tail target length is ${tail}, str is ${tailStr} of length ${tailStr.length}`);
        let d = (timestamp || Date.now()).toString(36);
        if (d.length < 9) d = `0` + d;
        const preId = `${d}${tailStr.slice(0, tail)}`;
        // log(`result: ${preId}${checksum(preId)} (made of ${preId} and ${checksum(preId)})`);
        // dumped = true;
        return `${preId}${checksum(preId)}`;
    }
}

export function isValidId(candidate: string) {
    // console.log(`checksum for ${candidate.slice(-checksumSize)} is ${checksum(candidate.slice(-checksumSize))}\nfull: ${candidate}`);

    return candidate.slice(-checksumSize) == checksum(candidate.slice(0, -checksumSize));
}

export function sliceWithHash(str: string, length: number, hashSize = 1) {
    if (str.length <= length) return str;
    return str.slice(0, length) + checksum(str.slice(length), hashSize);
}

export function lewHash32(key: string, size: number) {
    const hash = Array(size).fill(1981),
        // tslint:disable-next-line: no-bitwise
        limitToBits = (x: number, bits: number) => x & (2 ** bits - 1),
        pass = (acc: number, val: number) => {
            // tslint:disable-next-line: no-bitwise
            acc += val; acc += acc << 10; acc ^= acc >> 6;
            return acc;
        };
    let i = key.length;
    while (i--) {
        const b = i % size;
        hash[b] = pass(hash[b], key.charCodeAt(i));
    }
    for (let u = size - 1; u >= 0; --u)
        for (i = 1; i < size; ++i) {
            hash[i] = pass(hash[i], hash[u]);
            hash[u] = pass(hash[u], hash[i]);
        }
    if (hash.length == 1) return [limitToBits(hash[0], 32)];
    return hash.map((x, i) => limitToBits(x, 32));
}

export function lewHashStr(key: string, size: number) {
    return lewHash32(key, size).map((a) => pad(num2base92(a), 5)).join(``);
}

export function lewHashStr36(key: string, size: number) {
    return lewHash32(key, size).map((a) => pad(a.toString(36), 6)).join(``);
}

export function mulberry32(a: number) {
    return () => {
        let t = (a += 0x6d2b79f5);
        // tslint:disable-next-line: no-bitwise
        t = Math.imul(t ^ (t >>> 15), t | 1);
        // tslint:disable-next-line: no-bitwise
        t ^= t + Math.imul(t ^ (t >>> 7), t | 61);
        // tslint:disable-next-line: no-bitwise
        return (t ^ (t >>> 14)) >>> 0;
    };
}

export function mulberry01(a: number) {
    const m = mulberry32(a);
    return () => m() / (2 ** 32);
}

export function seedIfNeeded32(probablySeed: number | null = null) {
    return probablySeed !== null
        ? probablySeed
        : Math.floor(Math.random() * (2 ** 32));
}

export async function allLimit<Input = any, Result = any>(
  limit: number,
  inputs: Input[] | Generator<Input>,
  mapFn?: (arg: Input) => Promise<Result>,
  options: {
    timeoutMs?: number; // per promise
    timeoutError?: Error;
    totalTimeoutMs?: number; // for all Promises
    totalTimeoutError?: Error;
    requireSuccess?: boolean; // if one promise rejects, stop all and throw - DEFAULT FALSE!
  } = {}
): Promise<Result[]> {
  let results = [];
  let iterator: Iterable<[number, Input]> = (function* () {
    let index = 0;
    for (let input of inputs) {
      yield [index++, input];
    }
  })();
  let error: Error = null;
  async function workerProcess(
    iterable: Iterable<[number, Input | Promise<Result>]>,
    workerId: number
  ): Promise<void> {
    try {
      if (iterable !== iterator) {
        throw new Error('iterable mismatch');
      }
      for (let [index, input] of iterable) {
        if (error) {
          break;
        }
        let promise: Promise<Result>;
        try {
          if (typeof mapFn === 'function') {
            promise = mapFn(input as Input);
          } else {
            promise = input as Promise<Result>;
          }
          results[index] = await (options.timeoutMs
            ? timeout(options.timeoutMs, promise, options.timeoutError)
            : promise);
        } catch (e) {
          if (options.requireSuccess) {
            error = e instanceof Error ? e : new Error(e);
            break;
          }
          results[index] = e;
        }
      }
    } catch (e) {
      e.message = `promiseAllLimit worker ${workerId} failed: ${e.message}`;
      throw e;
    }
  }

  const concurrency = Array(limit)
    .fill(iterator)
    .map((iterable, worker) => workerProcess(iterable, worker));
  const done = Promise.allSettled(concurrency);
  await (options.totalTimeoutMs
    ? timeout(options.totalTimeoutMs, done, options.totalTimeoutError)
    : done);
  if (error) {
    throw error;
  }
  return results;
}

