export function sendMessageToClient<D = any>(event: RealtimeEvent<D>) {
  let channel = event.scope;
  if (event.account) {
    channel += `:${event.account}`;
  }
  if (!socketIo) {
    createEmitter();
  }
  socketIo.to(channel).emit('message', event);
}

export function sendMessageToClientPubsub<D = any>(event: RealtimeEvent<D>) {
  return callPubSub('send-realtime-message', event);
}

/**
 * Returns a Redis client instance.
 * @param host - The host of the Redis server.
 * @param additionalOptions - Additional options for the Redis client.
 * @param additionalOptions.singleClient - If true, returns a single Redis client instance for the specified name.
 * @param additionalOptions.name - The name of the Redis client instance.
 * @returns The Redis client instance.
 */
export function getRedisClient(
  host,
  additionalOptions: { singleClient?: boolean; name?: string; forceCloud?: boolean } = {}
): RedisClient {
  const { singleClient, name = 'default' } = additionalOptions;
  const forceCloud = additionalOptions.forceCloud || process.env.FORCE_CLOUD === 'true';
  if (singleClient && redisClients[name]) return redisClients[name];
  const options: RedisClientOptions = {
    url: `redis://${isLocal && !forceCloud ? localHost : host}:6379`,
  };

  const client = createClient(options);
  client.on('error', (err) => {
    if (isLocal) {
      if (logged[name]) return;
      logged[name] = true;
    }
    logger.error(`Redis error <${options.url}>:`, err);
  });
  client.on('ready', () => {
    logger.info(`Redis ready <${options.url}>`);
  });
  client.on('reconnecting', () => {
    if (isLocal) {
      if (logged[name]) return;
      logged[name] = true;
    }
    logger.info(`Redis reconnecting <${options.url}>`);
  });
  if (singleClient) {
    redisClients[name] = client;
    redisClients[name].connect();
  }
  return client;
}

export async function retry(fn, opts?: asyncRetry.Options) {
  return await asyncRetry(
    async () => {
      return await fn();
    },
    {
      retries: 3,
      maxTimeout: 10000,
      onRetry: (e) => {
        logger.warn(`doing retry on error: ${e}`);
      },
      ...opts,
    }
  );
}

export function initRuntime(
  args: {
    disableDataDog?: boolean;
    tracerOptions?: TracerOptions;
    monitorEventLoop?: boolean;
    expressAutoWrap?: boolean;
  } = {}
) {
  if (didInit) {
    return require('../datadog/tracing').tracer;
  }
  didInit = true;
  if (process.env.TW_DD && process.env.IS_LOCAL) {
    throw Error(
      'initRuntime must be the first import and the function must be called before the other imports!'
    );
  }
  const { tracerOptions, monitorEventLoop, expressAutoWrap = true, disableDataDog } = args;
  const tracer = loadDataDog({ tracerOptions, disableDataDog });
  require('../otel').setupOtel();
  if (monitorEventLoop) {
    eventLoopMonitor();
  }
  wrapExpress = expressAutoWrap;
  return { tracer };
}

