export function isProviderConnectedToShop(shopData: Shop, providerId: ServicesIds): boolean {
  try {
    if (!shopData) {
      logger.warn(`isProviderConnectedToShop: shopData is undefined`, { providerId });
      throw new Error('shopData is undefined');
    }

    if (services[providerId].getShopProviderStatus) {
      const providerStatus = services[providerId].getShopProviderStatus(shopData);
      return (
        providerStatus.status != ShopProviderStatusEnum.disconnected && !providerStatus.errorMessage
      );
    }

    if (services[providerId].getAccessToken && !services[providerId].getAccessToken(shopData)) {
      logger.info(
        `isProviderConnectedToShop: shop has no AccessToken: ${shopData.id} serviceId: ${providerId}`
      );
      return false;
    }

    if (!services[providerId].getAccounts(shopData)?.length) {
      logger.info(`isProviderConnectedToShop: shop has no Accounts, serviceId: ${providerId}`);
      return false;
    }

    if (services[providerId].getIsConnected && !services[providerId].getIsConnected(shopData)) {
      logger.info(
        `isProviderConnectedToShop: shop has invalid connection, providerId: ${providerId}`
      );
      return false;
    }
  } catch (error) {
    logger.error('isProviderConnectedToShop failed', { providerId, error });
  }

  return true;
}

export async function saveIntegrationStatusForShop(
  shopId: string,
  providerId: string,
  integrationId: string,
  status: ShopIntegrationStatusEnum,
  errorMessage?: string
): Promise<void> {
  try {
    const shopRef = getShop(shopId);
    const integrationUpdate = {
      status,
    };

    if (errorMessage) {
      integrationUpdate['error'] = {
        errorMessage,
        createdAt: firestore.FieldValue.serverTimestamp(),
      };
    }
    await shopRef.set(
      {
        providers: {
          [providerId]: {
            integrations: {
              [integrationId]: integrationUpdate,
            },
          },
        },
      },
      { merge: true }
    );
    logger.info(`saveIntegrationStatusForShop account ${integrationId}:`, {
      shopId,
      providerId,
      integrationId,
      status,
    });
    if (status == ShopIntegrationStatusEnum.error) {
      const connectionErrorMsg = errorMessage ? `Connection Error:  ${errorMessage}` : '';
      await callPubSub('new_activities_created', {
        shopId,
        serviceId: 'triple-whale',
        activities: [
          {
            type: 'update',
            date: new Date(),
            serviceId: 'triple-whale',
            annotation: false,
            shopId,
            entity: 'integration',
            source: providerId,
            title: 'Integration Connection Error',
            description: `${startCase(
              providerId
            )} Connection Error - Please Reconnect, ${connectionErrorMsg}`,
            id: `triple-whale_${providerId}_integration_connection_error_${new Date()}`,
          },
        ],
      });
    }
  } catch (error) {
    logger.error(`saveIntegrationStatusForShop failed account ${integrationId}:`, {
      error,
      shopId,
      providerId,
      integrationId,
      status,
    });
  }
}

