/**
 *
 * @param filePrefix `data-lake-facebook-ads-shofifi/facebook-ads/ads-metrics/act_1005998533610085/2022/07/14/`
 */
export async function softReimport(
  bucketName: string,
  filePrefix: string,
  jobHour = '23',
  useDayLatest = false
): Promise<boolean> {
  const options: GetFilesOptions = {
    prefix: filePrefix + (useDayLatest ? '' : jobHour + '/'),
  };

  try {
    const storageClient = getStorageClient();
    const [files] = await storageClient.bucket(bucketName).getFiles(options);

    if (files.length) {
      // name of file is timestamp so is ordered chronologically
      const lastUpdatedFile = files.reduce(
        (acc, curr) => (acc.name.split('/').pop() > curr.name.split('/').pop() ? acc : curr),
        {
          name: '0',
        } as File
      );
      const filename = `${Date.now()}-softreimport-${(Math.random() + 1)
        .toString(35)
        .substring(10)}.json`;

      const destFileName = `${filePrefix}${jobHour}/${filename}`;
      await lastUpdatedFile.copy(storageClient.bucket(bucketName).file(destFileName), {
        contentType: 'application/json',
        contentEncoding: 'gzip',
        metadata: {
          ...lastUpdatedFile.metadata.metadata,
          jobUTCDate: moment().utc().format(),
        },
      });
      return true;
    } else {
      return false;
    }
  } catch (e) {
    console.error(e);
    return false;
  }
}

export const splitArrayToChunks = <T = any>(array: T[] = [], chunk_size: number) => {
  return Array(Math.ceil(array.length / chunk_size))
    .fill(null)
    .map((_, index) => index * chunk_size)
    .map(begin => array.slice(begin, begin + chunk_size));
}

export function array2list(arr: string[]): string {
  const copy = [...arr];
  let str = copy.length > 1 ? 'and ' : '';
  str += copy.pop();
  while (copy.length) {
    str = copy.pop() + ', ' + str;
  }
  return str;
}

export function separate(str: string): string[] {
  return str
    .split(/(?=[A-Z])/)
    .map((sub) => sub.split(/[\(\s\)\-_]/))
    .flat()
    .filter(Boolean)
    .map((s) => s.toLowerCase());
}

export function capitalize(str: string): Capitalize<string> {
  return (str.charAt(0).toUpperCase() + str.slice(1)) as Capitalize<string>;
}

export function camel(str: string): string {
  return separate(str)
    .map((s, i) => (i === 0 ? s : capitalize(s)))
    .join('') as `${string}${Capitalize<string>}`;
}

export function pascal<S extends string = string>(str: S): Capitalize<S> {
  return separate(str).map(capitalize).join('') as Capitalize<S>;
}

export function kebab(str: string): string {
  return separate(str).join('-');
}

export function snake(str: string): string {
  return separate(str).join('_');
}

