}

export function mapResponseToSource(response: string): string {
  let token = findToken(response);
  return toServiceMap[token].source;
}

export function mapSourceToSummaryService(source: string): string {
  return toServiceMap[source]?.summaryPageService || toServiceMap[source]?.service || 'other';
}

export function detectLanguage(defaultQuestion: string): Language {
  for (const [language, translation] of Object.entries(tripleSurveyQuestionTranslation)) {
    if (translation === defaultQuestion) {
      return language as Language;
    }
  }
}

export function toArray(
  snapshot: firebase.firestore.QuerySnapshot<FirebaseFirestore.DocumentData> | []
) {
  const docs: any[] = [];

  snapshot.forEach((doc) => {
    docs.push({
      ...doc.data(),
      id: isNaN(+doc.id) ? doc.id : +doc.id,
    });
  });
  return docs;
}

/**
 * Same as Number.toFixed but returns number instead of string
 * @param num the number to fixed
 * @param places places to fix
 * @returns {number} the fixed number
 */
export function toFixed(num: number, places = 2): number {
  const factor = Math.pow(10, places);
  return Math.round((num + Number.EPSILON) * factor) / factor;
}

export function toHash(
  snapshot: firebase.firestore.QuerySnapshot<FirebaseFirestore.DocumentData> | []
) {
  var docs = {};
  snapshot.forEach((doc) => {
    docs[doc.id] = {
      ...(typeof doc.data == 'function' ? doc.data() : doc),
      id: isNaN(+doc.id) ? doc.id : +doc.id,
    };
  });
  return docs;
}

export interface RequestGlobalKeys {
  logParams?: {
    shopId?: string;
    [traceIdFieldName]?: string;
    pubSubMessageId?: string;
    userId?: string;
    wsEventName?: string;
    wsEventId?: string;
    wsSocketId?: string;
  };
  context?: {
    req?: Request;
    socket?: Socket;
    traceId?: string;
    pubsubMessage?: Message;
    spanContext?: SpanContext;
    parentLogParams?: Record<string, any>;
    uf: boolean;
  };
  contextInput?: any;
}

export const asyncLocalStorage = globalThis[
  twContextSymbol
] as AsyncLocalStorage<RequestGlobalKeys>;

export function getStore() {
  return asyncLocalStorage.getStore() || {};
}

export function getStoreKey<T extends keyof RequestGlobalKeys>(key: T): RequestGlobalKeys[T] {
  const store = getStore();
  const value = store[key];
  if (!value) {
    store[key] = {};
  }
  return store[key];
}

export function setStoreKey<T extends keyof RequestGlobalKeys>(
  key: T,
  value: RequestGlobalKeys[T]
): void {
  const store = getStore();
  store[key] = value;
}

export function startTrace() {
  const logParams = getStoreKey('logParams');
  const context = getStoreKey('context');
  const traceId = createTraceId();
  logParams[traceIdFieldName] = createTraceField(traceId);
  context.traceId = traceId;
}

export function createTraceId() {
  const traceId = Array.from({ length: 32 }, () =>
    Math.floor(Math.random() * 16).toString(16)
  ).join('');
  return traceId;
}

export function createTraceField(traceId: string) {
  return `projects/${projectId}/traces/${traceId}`;
}

export async function validateFFNewIntegration(
  shopId: string,
  serviceId: ServicesIds
): Promise<boolean> {
  try {
    const {
      data: { rankedControlList: serviceList },
    } = await callServiceEndpoint<FeatureFlagResultProperties>(
      'subscription-manager',
      `features/feature-flag-config/${shopId}/${FeatureFlag.LIMIT_INTEGRATIONS_FF}`,
      null,
      { method: 'GET' }
    );
    if (serviceList?.find((service) => service.id === serviceId)?.type === 'block') {
      throw new Error('Integration is blocked in this plan');
    }
    return true;
  } catch (err) {
    logger.error('Error in checkFFBeforeIntegration', err);
    return false;
  }
}

